    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        // TODO add your handling code here:
        // B1: xóa các items mac dinh;
        // B2: them ca items cua nguoi dung;

        this.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        this.setLocationRelativeTo(null);

        cbo_queQuan.removeAllItems(); // xoa het cac item mac dinh
        cbo_queQuan.addItem("Ha Noi");
        cbo_queQuan.addItem("Hai Phong");
        cbo_queQuan.addItem("Nam Dinh");
        cbo_queQuan.addItem("Ho Chi Minh");
        cbo_queQuan.addItem("Bac Giang");
    }//GEN-LAST:event_formWindowOpened

