    private void btn_themActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_themActionPerformed
        // TODO add your handling code here:
        
        int i = 0; // kiem tra neu da co thi khong them vao
        String kiemTra = txt_them.getText();
        for (int j = 0; j < cbo_queQuan.getItemCount(); j++) {
            if(kiemTra.equalsIgnoreCase(cbo_queQuan.getItemAt(j))) {
                i++;
            }
        }
        
        if (i == 0) {
            cbo_queQuan.addItem(txt_them.getText());
            txt_them.setText("");
            JOptionPane.showMessageDialog(this, "Ban da them mot Item");
        } else {
            JOptionPane.showMessageDialog(this, "Da co!");
        }
    }//GEN-LAST:event_btn_themActionPerformed


