    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        // TODO add your handling code here:
        this.setLocationRelativeTo(null);
        this.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        this.setTitle("Quan ly sinh vien");
        
        
        cbo_nganh.removeAllItems();
        cbo_nganh.addItem("Cong nghe thong tin");
        cbo_nganh.addItem("Ung dung phan men");
        cbo_nganh.addItem("Thiet ke do hoa");
        cbo_nganh.addItem("Lap trinh may tinh");
        
        Student sv = new Student("Nguyen chi thuan", 9.9, "Cong nghe thong tin");
        mangSinhVien.add(sv);
        fillTable();
    }//GEN-LAST:event_formWindowOpened

