    ArrayList<Student> mangSinhVien = new ArrayList<>();
    
    public void fillTable() {
        DefaultTableModel model = (DefaultTableModel) tbl.getModel();
        
        model.setRowCount(0);
        
        for (Student s : mangSinhVien) {
            model.addRow(new Object[] {
                s.getTen(), s.getDiem(), s.getNganh(), s.getHocLuc(), s.kiemTraThuong()
            });
        }
    }
    
    
    // lay du lieu tu form
    public Student readStudent() { 
        // ta chi can lay ten, diem, nganh
        String ten = txt_ten.getText();
        Double diem = Double.parseDouble(txt_diem.getText());
        String nganh = cbo_nganh.getSelectedItem() + "";
        
        return new Student(ten, diem, nganh);
    }
    
    // doc du lieu vao form
    public void writeStudent(Student sv) {
        txt_ten.setText(sv.getTen());
        txt_diem.setText(sv.getDiem() + "");
        for (int i = 0; i < cbo_nganh.getItemCount(); i++) {
            if(cbo_nganh.getItemAt(i).equalsIgnoreCase(sv.getNganh())) {
                cbo_nganh.setSelectedIndex(i);
            }
        }
        txt_hocLuc.setText(sv.getHocLuc());
        chk_thuong.setSelected(sv.kiemTraThuong());
    }
    
    public void themSinhVien() {
        mangSinhVien.add(readStudent());
        fillTable();
    }
    
    public int indexTable() {
        return tbl.getSelectedRow();
    }
    
    public void xoaSinhVien() {
        mangSinhVien.remove(indexTable());
        this.fillTable();
    }
    
    public void capNhat() {
        mangSinhVien.set(indexTable(), readStudent());
        this.fillTable();
    }
    
    public void lamMoi() {
        txt_ten.setText("");
        txt_diem.setText("");
        txt_hocLuc.setText("");
        chk_thuong.setSelected(false);
    }
    
    public void sapXepTheTen() {
        Collections.sort(mangSinhVien, new Comparator<Student>() {
            @Override
            public int compare(Student o1, Student o2) {
                return o1.getTen().compareToIgnoreCase(o2.getTen());
            }
        });
    }
    
    public void sapXepTheoDiem() {
        Collections.sort(mangSinhVien, new Comparator<Student>() {
            @Override
            public int compare(Student o1, Student o2) {
                if(o1.getDiem() > o2.getDiem()) {
                    return 1;
                } else if(o1.getDiem() < o2.getDiem()) {
                    return -1;
                } else {
                    return 0;
                }
            }
        });
    }
    
    

